package com.exmaple.jarvis.running.Dialog;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.v4.app.DialogFragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.exmaple.jarvis.running.R;public class EditDialog extends DialogFragment {    private TextView et_location;    private String mLocation;    @Override    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        View v = inflater.inflate(R.layout.dialog_edit_location, container, false);        getBundle();        initView(v);        setOnClick();        return v;    }    private void getBundle() {        Bundle mArgs = getArguments();        mLocation = mArgs.getString("location");    }    private void initView(View v) {        et_location = v.findViewById(R.id.et_location);        et_location.setText(mLocation);    }    private void setOnClick() {        et_location.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {            }        });    }}